int getSuccessorIndex(int partIndex){
	if(partIndex = 3){
		return 0;
	}
	return partIndex +1;
}

int getPrecedessorIndex(int partIndex){
	if(partIndex = 0){
		return 3;
	}
	return partIndex -1;
}

